    @Rule
    public final MockitoComponentMockingRule<PrivateKeyPasswordBasedEncryptor> mocker = new MockitoComponentMockingRule<PrivateKeyPasswordBasedEncryptor>(DefaultPrivateKeyPasswordBasedEncryptor.class);

    PrivateKeyPasswordBasedEncryptor encryptor;

    PrivateKeyParameters keyParameters;

    @Before
    public void configure() throws Exception {
        this.encryptor = this.mocker.getComponentUnderTest();
        this.keyParameters = ((AsymmetricKeyFactory) (this.mocker.getInstance(AsymmetricKeyFactory.class))).fromPKCS8(RSAKEY);
    }

    private void runTestPBES2Conformance(String hint, String iv, int keySize, String salt, byte[] data) throws Exception {
        Assert.assertThat(this.encryptor.decrypt(PASSWORD, data).getEncoded(), CoreMatchers.equalTo(RSAKEY));
        if (!(IS_JRE_8)) {
            Assert.assertThat(this.encryptor.decrypt(PASSWORD, new EncryptedPrivateKeyInfo(data)).getEncoded(), CoreMatchers.equalTo(RSAKEY));
        }
        Assert.assertThat(this.encryptor.encrypt(hint, new KeyWithIVParameters(PASSWORD, Hex.decode(iv)), new PBKDF2Parameters(keySize, 2048, Hex.decode(salt)), this.keyParameters), CoreMatchers.equalTo(data));
    }

