    @Test
    public void testEncryptDecryptPBES2AES() throws Exception {
        Assert.assertThat(this.encryptor.decrypt(PASSWORD, this.encryptor.encrypt("PBES2-AES-CBC-Pad", PASSWORD, new PBKDF2Parameters(), this.keyParameters)).getEncoded(), CoreMatchers.equalTo(RSAKEY));
    }

